    private void bncalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bncalcularActionPerformed
        float Num1;
        float Num2;
        float Num3;
        float Total;
        
        Num1=Float.parseFloat(CajaNum1.getText());
        Num2=Float.parseFloat(CajaNum2.getText());
        Num3=Float.parseFloat(CajaNum3.getText());
        
        Total=Num1+Num2-Num3;
        
        txtTotal.setText(String.valueOf(Total));
        
    }//GEN-LAST:event_bncalcularActionPerformed

